mapper.select:
SELECT * FROM street_lamp.test_tb_failure_history

	WHERE beacon_addr = #{beacon_addr}
	AND beacon_id = #{beacon_id}
	AND location = #{location}
	AND failure_reason_id = #{failure_reason_id}
	AND failure_type = #{failure_type}
	AND repair = #{repair}
	AND recent = #{recent}

