mapper.select:
SELECT * FROM street_lamp.user

	WHERE user_id = #{user_id}
	AND user_password =
	#{user_password}

mapper.update:
UPDATE
street_lamp.user SET
user_contact=#{user_contact}
WHERE user_id=#{user_id}

UPDATE
street_lamp.user SET
user_password=#{user_password}
WHERE user_id=#{user_id}

UPDATE
street_lamp.user SET
user_album=#{user_album}
WHERE user_id=#{user_id}
